package com.hefshine.ForLoop;

public class Pattern17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int cnt=0;
		for(int i=0; i<=4; i++)
		{
			for(int j=0;j<=i;j++)
			{
				
				System.out.print(" 1 ");
				cnt=cnt-1;
			}
			System.out.println("");
		}

	}

}
