package com.hefshine.ForLoop;

public class Pattern4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, j,count=0;
		for(j=1;j<=4;j=j+1) 
		{
			for(i=1;i<=4;i=i+1) 
			
		{
			count=count+1;
			System.out.print(count+ " ");
			
			}
			System.out.println(" ");
		}
	}
}
	
	


